import { CharaList } from '../helpers/charalist'
import '../styles/chara.css'
import React, { Component } from 'react';
import Characterdata from './Charadata';
import Weapondata from './Weapondata';
import '../styles/adddata.css';

class Charas extends Component {
  constructor() {
    super();
    this.state = {
      showModal: false
    };
  }

  showModal = (e) => {
    this.setState(prevState => ({
        showModal: !prevState.showModal 
    }));
  }



  render() {
    return (
      //     <div class='Characters'>
      //         <h1>
      //             Characters
      //         </h1>
    <div className="charalist">
             {CharaList.map((Charaitem, key)=>{
                return (
                  <div>
                    <h1> Add Data</h1>
                    <div className='charaItem'>
                    <button onClick={this.showModal}>
                          <div style={{backgroundImage:`url(${Charaitem.image})`}}></div>
                          <p>{Charaitem.name}</p>
                    </button>

                    {this.state.showModal && 
                      <Characterdata
                        onCloseModal={this.showModal} 
                      />
                    }
                    </div>
                    
                    <div>
                    <button onClick={this.showModal}>
                      Weapon
                    </button>

                    {this.state.showModal && 
                      <Weapondata
                        onCloseModal={this.showModal} 
                      />
                    }
                    </div>

                  </div>)})}
        </div>
    
    
  )}
}

