import React, { Component } from 'react';
import { render } from 'react-dom';
import Characterdata from './Charadata';
import Weapondata from './Weapondata';
import '../styles/adddata.css';

class Adddata extends Component {
  constructor() {
    super();
    this.state = {
      open: false,
      data: [ "Character", "Weapon", "Region","Weapon Domain", "Talents", "Character Ascension Materials","Weapon Ascension Materials","Information"],
      groupname: ""
    };
  }

  Open(grp, e) {
    this.setState({
      groupname: grp,
      open: !this.state.open
    });
  }

  Close = () => {
    this.setState({
       open: !this.state.open
    });
  }




  render() {
    return (
      <div>
        <h1> Add Data</h1>
        <div>
      {this.state.data.map((element, index) => {
      return <span  key={index} ><button type="button" onClick={this.Open.bind(this, element)}>{element}</button><br/><br/></span>
      })}
      {this.state.open &&
        <Characterdata
          modalBsClass={this.state.groupname}
          modalSizeClass="modal-md full-height-modal-right"
          modalHeaderTitle="Modal"
          modalBody={ <div><input type="text" value={this.state.name} onChange={this.handleChange} /> 
          <p>{this.state.groupname}</p></div>
          }
          onClick={this.Close}
          save={this.Save}
          inputRef={this.setWrapperRef}
        />}
      </div>
      </div>
      
    );
  }
}

// render(<App />, document.getElementById('root'));
export default Adddata;
