import React from 'react'
import { WeaponList } from '../helpers/weaponlist'
import Weaponinfo from './Weaponinfo';
import '../styles/chara.css'
import { Component } from 'react';

class Weapons extends Component {
  constructor() {
    super();
    this.state = {
      showModal: false
    };
  }

  showModal = (e) => {
    this.setState(prevState => ({
        showModal: !prevState.showModal 
    }));
  }



  render() {
    return (

      <div>
        <div><h1> Characters</h1></div>
        <div>
        
          <div class='Weapons'>
            <div className="weaponlist">
                {WeaponList.map((Weaponitem, key)=>{
                    return (
                        <div className='weaponItem'>
                            <div style={{backgroundImage:`url(${Weaponitem.image})`}}></div>
                            <p>{Weaponitem.name}</p>
                        </div>
                      )
                    }
                  )}
            </div>
        </div>
        

        {this.state.showModal && 
          <Weaponinfo
            onCloseModal={this.showModal} 
          />
        }
        </div>
        

      </div>
      
    );
  }
}

// render(<App />, document.getElementById('root'));
export default Weapons;





